"use strict";
// Basic Data Types
// String
let firstName = 'John'; // explicit type declaration
let lastName = 'Doe'; // implicit type declaration
// Number
let roll = 101; // explicit type declaration
let marks = 80; // implicit type declaration
// Boolean
let isPassed = true; // explicit type declaration
let isFailed = false; // implicit type declaration
// Undefined
let x = undefined; // explicit type declaration
let y = undefined; // implicit type declaration
// Null
let z = null; // explicit type declaration
let a = null; // implicit type declaration
let randomValue;
randomValue = 'John';
randomValue = true;
